import React from "react";


class IdadeDeFuano extends React.Component {
  render() {
    const { value, handleChange} = this.props;

    let error = undefined;
    if (value.length > 20) error = 'Nome muito grande';

    return(
      <label>
          Diga sua idade:
          <input 
            type="number"
            name='idade'
            value={value}
            onChange={handleChange}
          />
          <span>{error !== undefined ? error : ''}</span>
      </label>
    )
  }
}

export default IdadeDeFuano;
