class NomeDoFulano extends Component {

  render() {
    const { handleChange, value} = this.props;

    let error = undefined;
    if (value.length > 20) error = 'Nome muito grande';

    return (
      <labe>
        Diga seu nome:
        <input
          type="text"
          name='nome'
          value={value}
          onChange={handleChange}
        />
      </labe>
    )
  }

}


export default NomeDoFulano;
