import React from "react";

class UltimoEmprego extends React.Component {

  onMouse() {
    document.getElementById('onmouse').style.backgroundColor = "black";
  }

  render() {
    return (
      <fieldset>
        <label>
          Resumo do Curriculo:
          <textarea maxLength="1000" required />
        </label>
        <label>
          Cargo:
          <textarea maxLength="40" onmouseenter={this.onMouse} required/>
          <span id="onmouse"></span>
        </label>
        <label>
          Descrição do cargo:
          <input type="text" maxLength="500" required/>
        </label>
      </fieldset>
    )
  }
}

export default UltimoEmprego;
