import React, { Component } from 'react'


class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      nome: '',
      idade: 0,
      boleano: false
    };
  }


  handleChange({target}) {
    const { name } = target;
    const value = target.type == 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    })
  }

  render() {
    return (
      <div>
        <h1>Estados e React - Tecnologia fantástica ou reagindo a regionalismos?</h1>
        <form className="form">
          <labe>
            <p>Diga seu nome:</p>
            <input 
              type="text"
              value={this.state.nome}
              onChange={this.handleChange}
            />
          </labe>
          <label>
            <p>Diga sua idade:</p>
            <input type="number"
              value={this.state.idade}
              onChange={this.handleChange}
            />
          </label>
          <label>
            <p>Você gosta de chocolate?</p>
            <input 
              type="checkbox" 
              value={this.state.boleano}
            />
          </label>
        </form>
      </div>
    );
  }
}

export default Form;
