import React from 'react';

class DadosPessoais extends React.Component {
  constructor() {
    super()

    this.state = {
      nome: '',
      email: '',
      cpf: '',
      endereco: '',
      cidade: '',
    }
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange({ target }) {

    const { name, type, value } = target;

    this.setState({
      [name]: value
    })
  }

  render() {
    const estados = ['AC', 'AL', 'AP', 'AM', 'BA', 'CE', 'DF', 'ES', 'GO', 'MA', 'MT', 'MS', 'MG', 'PA', 'PB', 'PR', 'PE', 'PI', 'RJ', 'RN', 'RS', 'RO', 'RR', 'SC', 'SP', 'SE', 'TO']
    return (
      <fieldset>
        <label>
          Nome:
          <input type="text" name="nome" onChange={this.handleChange} required />
        </label>
        <label>
          Email:
          <input type="email" name="email" onChange={this.handleChange} required />
        </label>
        <label>
          CPF:
          <input type="text" name="cpf" onChange={this.handleChange} required />
        </label>
        <label>
          Endere√ßo:
          <input type="text" name="endereco" onChange={this.handleChange} required />
        </label>
        <label>
          Cidade:
          <input type="text" name="cidade" onChange={this.handleChange} required />
        </label>
        <label>
          Estado:
          <select onChange={this.handleChange}>
            {estados.map((estado) => {
              return <option name={estado} key={estado} >{estado}</option>
            })}
          </select>
        </label>
        <div>
          <label for="casa">
            Casa:
          </label>
          <input id="casa" type="radio" name="moradia" value="casa" />
        </div>
        <div>
          <label for="apartamento">
            Apartamento:
          </label>
          <input id="apartamento" type="radio" name="moradia" value="apartamento" />
        </div>

      </fieldset>
    )
  }
}
export default DadosPessoais;
