import React, { Component } from 'react'
import NomeDoFulano from './NomeDoFulano';
import IdadeDeFuano from './IdadeDeFulano';

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      nome: '',
      idade: 0,
      boleano: false
    };
  }


  handleChange({target}) {
    console.log(target);
    const { name } = target;
    const value = target.type == 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    })
  }

  render() {
    return (
      <div>
        <h1>Estados e React - Tecnologia fantástica ou reagindo a regionalismos?</h1>
        <form className="form">
          <NomeDoFulano handleChange={this.handleChange} value={this.state.nome} />
          <IdadeDeFuano handleChange={this.handleChange} value={this.state.idade} />
          <label>
            <p>Você gosta de chocolate?</p>
            <input 
              type="checkbox"
              name='boleano'
              value={this.state.boleano}
              onChange={this.handleChange}
            />
          </label>
        </form>
      </div>
    );
  }
}

export default Form;
