import React, { Component } from 'react'
import NomeDoFulano from './NomeDoFulano';
import IdadeDeFuano from './IdadeDeFulano';
import Boleano from './Boleano';

class Form extends Component {
  constructor() {
    super();

    this.handleChange = this.handleChange.bind(this);

    this.state = {
      nome: '',
      idade: 0,
      boleano: false
    };
  }


  handleChange({target}) {
    console.log(target);
    const { name } = target;
    const value = target.type == 'checkbox' ? target.checked : target.value;

    this.setState({
      [name]: value,
    })
  }

  render() {
    return (
      <div>
        <h1>Estados e React - Tecnologia fant√°stica ou reagindo a regionalismos?</h1>
        <form className="form">
          <NomeDoFulano handleChange={this.handleChange} value={this.state.nome} />
          <IdadeDeFuano handleChange={this.handleChange} value={this.state.idade} />
          <Boleano handleChange={this.handleChange} value={this.state.boleano} />
        </form>
      </div>
    );
  }
}

export default Form;
