import React from "react";


class IdadeDeFuano extends React.Component {
  render() {
    const { value, handleChange} = this.props;
    return(
      <label>
            <p>Diga sua idade:</p>
            <input 
              type="number"
              name='idade'
              value={value}
              onChange={handleChange}
            />
          </label>
    )
  }
}

export default IdadeDeFuano;
